-- Create table for Vietnamese recipes
CREATE TABLE vietnamese_recipes (
    recipe_id VARCHAR(20) PRIMARY KEY,
    recipe_name VARCHAR(100) NOT NULL,
    description TEXT,
    image_file VARCHAR(255)
);

-- Create table for Chinese recipes
CREATE TABLE chinese_recipes (
    recipe_id VARCHAR(20) PRIMARY KEY,
    recipe_name VARCHAR(100) NOT NULL,
    description TEXT,
    image_file VARCHAR(255)
);

